import React from "react";
import { validate } from "@babel/types";

const Course = props => {
  const { course } = props;

  const { parts } = course;

  const array1 = [1, 2, 3, 4];
  const reducer = array1.reduce((acc, cur) => {
    return acc + cur;
  });
  console.log(reducer);

  console.log(parts);
  const total = parts.reduce((s, p) => {
    return { x: s.exercises + p.exercises };
  });

  console.log(total);

  const initialValue = 0;
  const sum = [
    { x: 1, y: 2 },
    { x: 2, y: 3 },
    { x: 3, y: 4 }
  ].reduce((accumulator, currentValue) => {
    return accumulator + currentValue.y;
  }, initialValue);

  console.log(sum); // logs 6
  const rows = () =>
    course.parts.map(el => (
      <p key={el.id}>
        {el.name} {el.exercises}
      </p>
    ));

  return (
    <div>
      <h1>{course.name}</h1>
      {rows()}
    </div>
  );
};
