import React from "react";

const Course = props => {
  const { course } = props;

  const total = course.parts.exercises.reduce((previous, current) => {
    return previous + current;
  });
  console.log(total);
  const rows = () =>
    course.parts.map(el => (
      <p key={el.id}>
        {el.name} {el.exercises}
      </p>
    ));

  return (
    <div>
      <h1>{course.name}</h1>
      {rows()}
    </div>
  );
};
